import React, {Component} from 'react'
import {bindActionCreators} from "redux";
import {clearData, fetchUser, fetchUserFollow, fetchUserPosts} from "../Redux/action";
import {connect} from 'react-redux'
import {createMaterialBottomTabNavigator} from "@react-navigation/material-bottom-tabs";
import FeedScreen from './main/Feed'
import ProfileScreen from './main/Profile'
import SearchScreen from "./main/Search";
import firebase from "firebase";
import MaterialCommunityIcon from 'react-native-vector-icons/MaterialCommunityIcons'

const Tab = createMaterialBottomTabNavigator()

const EmptyScreen = () => {
    return (null)
}

export class Main extends Component {
    componentDidMount() {
        this.props.clearData()
        this.props.fetchUser()
        this.props.fetchUserPosts()
        this.props.fetchUserFollow()
    }

    render() {
        return (
            <Tab.Navigator InitializeRouteName="Feed" labeled={false}>
                <Tab.Screen name="Feed" component={FeedScreen}
                            options={{
                                tabBarIcon: ({color, size}) => (
                                    <MaterialCommunityIcon name="home" color={color} size={26}/>
                                ),
                            }}/>
                <Tab.Screen name="Search" component={SearchScreen} navigation={this.props.navigation}
                            options={{
                                tabBarIcon: ({color, size}) => (
                                    <MaterialCommunityIcon name="magnify" color={color} size={26}/>
                                ),
                            }}/>
                <Tab.Screen name="AddContainer" component={EmptyScreen}
                            listeners={({navigation}) => ({
                                tabPress: event => {
                                    event.preventDefault()
                                    navigation.navigate("Add")
                                }
                            })}
                            options={{
                                tabBarIcon: ({color, size}) => (
                                    <MaterialCommunityIcon name="plus-box" color={color} size={26}/>
                                ),
                            }}/>
                <Tab.Screen name="Profile" component={ProfileScreen}
                            listeners={({navigation}) => ({
                                tabPress: event => {
                                    event.preventDefault()
                                    navigation.navigate("Profile", {uid: firebase.auth().currentUser.uid})
                                }
                            })}
                            options={{
                                tabBarIcon: ({color, size}) => (
                                    <MaterialCommunityIcon name="account-circle" color={color} size={26}/>
                                ),
                            }}/>
            </Tab.Navigator>
        )
    }
}

const mapStateToProps = (store) => ({
    currentUser: store.userState.currentUser
})
const mapDispatchProps = (dispatch) => bindActionCreators({fetchUser, fetchUserPosts, fetchUserFollow, clearData}, dispatch)
export default connect(mapStateToProps, mapDispatchProps)(Main)
