import firebase from "firebase";
import {USER_FOLLOW_STATE_CHANGE, USER_POST_STATE_CHANGE, USER_STATE_CHANGE} from "../const";

export function fetchUser() {
    return ((dispatch) => {
            firebase.firestore()
                .collection("users")
                .doc(firebase.auth().currentUser.uid)
                .get()
                .then((snapshot) => {
                    if (snapshot.exists) {
                        dispatch({ type: USER_STATE_CHANGE, currentUser: snapshot.data() })
                    }
                    else {
                        console.log('does not exist')
                    }
                })
        })
}

export function fetchUserPosts() {
    return ((dispatch) => {
            firebase.firestore()
                .collection("posts")
                .doc(firebase.auth().currentUser.uid)
                .collection("userPosts")
                .orderBy("creation","asc")
                .get()
                .then((snapshot) => {
                    let posts = snapshot.docs.map(doc => {
                        const data = doc.data()
                        const id = doc.id
                        return{id, ...data}
                    })
                    debugger
                    dispatch({ type: USER_POST_STATE_CHANGE, posts })
                })

        })
}
export function fetchUserFollow() {
    return ((dispatch) => {
            firebase.firestore()
                .collection("following")
                .doc(firebase.auth().currentUser.uid)
                .collection("userFollowing")
                .onSnapshot((snapshot) => {
                    let following = snapshot.docs.map(doc => {
                        return doc.id
                    })
                    debugger
                    dispatch({ type: USER_FOLLOW_STATE_CHANGE, following })
                })

        })
}


