import React, {useState, useEffect} from 'react'
import {View, Text, Image, FlatList, StyleSheet, Button} from 'react-native'
import {connect} from 'react-redux'
import photo from '../../assets/splash.png'
import firebase from "firebase";

function Feed(props) {
    const [posts, setPosts] = useState([])
    useEffect(() => {
        if (props.userFollowingLoaded === props.following.length && props.following.length) {
            props.feed.sort(function (x, y) {
                return x.creation - y.creation
            })
            setPosts(props.feed)
        }
        console.log(posts)
    }, [props.userFollowingLoaded, props.feed])

    if (!posts.length) {
        return <View/>
    }
    debugger
    return (
        <View style={styles.container}>
            <View style={styles.containerGallery}>
                <FlatList
                    numColumns={1}
                    horizontal={false}
                    data={posts}
                    renderItem={({item}) => {
                        debugger
                        return (
                            <View
                                style={styles.containerImage}>
                                <Image
                                    style={styles.image}
                                    source={{uri: item.downloadUrl}}
                                />
                                <Text
                                    onPress={() => props.navigation.navigate('Comment', {
                                        postId: item.id, uid: item.user.uid})}>Comments...</Text>
                            </View>

                        )
                    }}

                />
            </View>
        </View>
    )
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        marginTop: 40
    },
    containerInfo: {
        margin: 20
    },
    containerGallery: {
        flex: 1
    },
    image: {
        flex: 1,
        aspectRatio: 1 / 1
    },
    containerImage: {
        flex: 1 / 3
    }
})
const mapStateToProps = (store) => ({
    currentUser: store.userState.currentUser,
    following: store.userState.following,
    userFollowingLoaded: store.usersState.userFollowingLoaded,
    feed: store.usersState.feed
})
export default connect(mapStateToProps, null)(Feed)
