import React, {Component} from 'react';
import { StatusBar } from 'expo-status-bar';
import {NavigationContainer} from "@react-navigation/native";

import * as firebase from "firebase"
const firebaseConfig = {
    apiKey: "AIzaSyAubE6EFsXyQ_zSplh-AlI5BQNN-xLiK_E",
    authDomain: "instagram-fd6fb.firebaseapp.com",
    projectId: "instagram-fd6fb",
    storageBucket: "instagram-fd6fb.appspot.com",
    messagingSenderId: "662090284621",
    appId: "1:662090284621:web:f89f172c809ee4d36b6604",
    measurementId: "G-589K4GQ006"
};

if (firebase.apps.length === 0) {
    firebase.initializeApp(firebaseConfig)
}

import {createStackNavigator} from "@react-navigation/stack";
import LandingScreen from "./components/auth/LandingAuthor";
import Registration from "./components/auth/Registration";
import View from "react-native-web/dist/exports/View";
import Text from "react-native-web/src/exports/Text";


const Stack = createStackNavigator();
export default class  App extends Component{
    constructor(props) {
        super(props);
        this.state = {
            loaded:false,
            loggedIn:false
        }
    }

    componentDidMount() {
        firebase.auth().onAuthStateChanged(user => {
            if (!user){
                this.setState({
                    loaded:true,
                    loggedIn:false
                })
            } else {
                this.setState({
                    loaded:true,
                    loggedIn:true
                })
            }
        })
    }

    render() {
        const {loaded, loggedIn} = this.state
        if (!loaded){
            return <View style={{flex:1,justifyContent:'center'}}>
                <Text>
                    Loading
                </Text>
            </View>
        }

        if (!loggedIn){
            return (
                <NavigationContainer>
                    <Stack.Navigator initialRouteName={"Landing"}>
                        <Stack.Screen name={"Landing"} component={LandingScreen} options={{headerShown:false}} />
                        <Stack.Screen name={"Register"} component={Registration}/>
                    </Stack.Navigator>
                </NavigationContainer>
            );
        }

        return <View style={{flex:1,justifyContent:'center'}}>
            <Text>
                User logged In
            </Text>
        </View>

    }


}
