import {CLEAR_DATA, USER_FOLLOW_STATE_CHANGE, USER_POST_STATE_CHANGE, USER_STATE_CHANGE} from "../const";

const InitializeState = {
    currentUser: null,
    posts: [],
    following:[]
}

export const user = (state = InitializeState, action) => {
    switch (action.type) {
        case USER_STATE_CHANGE:
            return {
                ...state,
                currentUser: action.currentUser
            }
        case USER_POST_STATE_CHANGE:
            return {
                ...state,
                posts: action.posts
            }
        case USER_FOLLOW_STATE_CHANGE:
            return {
                ...state,
                following: action.following
            }
        case CLEAR_DATA:
        {
            return {
                currentUser: null,
                posts: [],
                following:[]
            }
        }
        default:
            return state
    }

}
