import {
    USERS_DATA_STATE_CHANGE,
    USERS_POST_STATE_CHANGE
} from "../const";
import {user} from "./user";

const InitializeState = {
    users: [],
    userLoaded: 0
}

export const users = (state = InitializeState, action) => {
    switch (action.type) {
        case USERS_DATA_STATE_CHANGE:
            return {
                ...state,
                users: [...state.users, action.user]
            }
        case USERS_POST_STATE_CHANGE:
            debugger
            return {
                ...state,
                userLoaded: state.userLoaded + 1,
                users: state.users.map(user => user.uid === action.uid ?
                    {...user,posts:action.posts} : user)
            }
        default:
            return state
    }

}
