import React,{useState} from 'react'
import {View, TextInput, Button, Image} from 'react-native'
import firebase from "firebase";
require("firebase/firestore")
require("firebase/firebase-storage")

export default function Save(props){
    const [caption,setCaption] = useState("")
    const childUrL = `post/${firebase.auth().currentUser.uid}/${Math.round().toString(36)}`
    const uploadImage = async () => {
        const uri = props.route.params.image
        const response = await fetch(uri)
        const blob = response.blob()
        const task = firebase.storage()
            .ref()
            .child(childUrL)
            .put(blob)

        const taskProgress = snapshot => {
            console.log(`transffered: ${snapshot.bytesTransferred}`)
        }

        const taskCompleted = snapshot => {
            console.log(snapshot)
        }

        const taskError = snapshot => {
            console.log(snapshot)
        }
        task.on("state_change",taskProgress,taskCompleted,taskError)
    }
    return(
        <View style={{flex:1}}>
            <Image source={{uri:props.route.params.image}}/>
            <TextInput placeholder={"Write a Caption"} onChange={(caption) => setCaption(caption)}/>
            <Button title="Save"/>
        </View>
    )
}
