import firebase from "firebase";
import {USER_STATE_CHANGE} from "../const";

export function fetchUser() {
    return (dispatch) => {
        try{
            firebase.firestore()
                .collection("user")
                .doc(firebase.auth().currentUser.uid)
                .get()
                .then((snapshot) => {
                    debugger
                    console.log(snapshot)
                    if (snapshot.exists){
                        dispatch({type:USER_STATE_CHANGE,currentUser:snapshot.data()})
                    }
                })
        }catch (e) {

        }
    }
}
