import React, {Component} from 'react'
import {Button, TextInput, View} from "react-native";
import firebase from "firebase";

export default class Registration extends Component {
    constructor(props) {
        super(props);
        this.state = {
            email: '',
            name: '',
            password: ''
        }
        this.onSingUp = this.onSingUp.bind(this)
    }

    onSingUp() {
        const {email, name, password} = this.state
        firebase.auth().createUserWithEmailAndPassword(email, password)
            .then(res => {
                firebase.firestore().collection("users")
                    .doc(firebase.auth().currentUser.uid)
                    .set({
                        name,
                        email
                    })
                console.log(res)
            })
            .catch(error => {
                console.log(error)
            })
    }

    render() {
        return <View>
            <TextInput placeholder={"name"} onChangeText={(name) => {
                this.setState({name})
            }}/>
            <TextInput placeholder={"email"} onChangeText={(email) => {
                this.setState({email})
            }}/>
            <TextInput placeholder={"password"} secureTextEntry={true} onChangeText={(password) => {
                this.setState({password})
            }}/>
            <Button title={"Sing Up"} onPress={() => {
                this.onSingUp()
            }}/>
        </View>
    }
}
