// Bismillah Rahman Rahim
import React, {Component} from 'react';
import { StatusBar } from 'expo-status-bar';
import {NavigationContainer} from "@react-navigation/native";

import * as firebase from "firebase"

import {Provider} from "react-redux";
import {createStore, applyMiddleware} from "redux";
import thunk from "redux-thunk";
import rootReducer from './Redux/reducers'


const store = createStore(rootReducer,applyMiddleware(thunk))
const state = window.store
const firebaseConfig = {
    apiKey: "AIzaSyAubE6EFsXyQ_zSplh-AlI5BQNN-xLiK_E",
    authDomain: "instagram-fd6fb.firebaseapp.com",
    projectId: "instagram-fd6fb",
    storageBucket: "instagram-fd6fb.appspot.com",
    messagingSenderId: "662090284621",
    appId: "1:662090284621:web:f89f172c809ee4d36b6604",
    measurementId: "G-589K4GQ006"
};

if (firebase.apps.length === 0) {
    firebase.initializeApp(firebaseConfig)
}

import {createStackNavigator} from "@react-navigation/stack";
import LandingScreen from "./components/auth/LandingAuthor";
import RegistrationScreen from "./components/auth/Registration";
import LogInScreen from "./components/auth/LogIn";
import {View, Text} from "react-native"
import MainScreen from "./components/Main";
import AddScreen from "./components/main/Add";
import SaveScreen from "./components/main/Save";
const Stack = createStackNavigator();
export default class  App extends Component{
    constructor(props) {
        super(props);
        this.state = {
            loaded:false,
            loggedIn:false
        }
    }

    componentDidMount() {
        firebase.auth().onAuthStateChanged(user => {
            if (!user){
                this.setState({
                    loaded:true,
                    loggedIn:false
                })
            } else {
                this.setState({
                    loaded:true,
                    loggedIn:true
                })
            }
        })
    }

    render() {
        const {loaded, loggedIn} = this.state
        if (!loaded){
            return  <View style={{flex:1,justifyContent:'center'}}>
                <Text>
                    Loading
                </Text>
            </View>
        }

        if (!loggedIn){
            return (
                <NavigationContainer>
                    <Stack.Navigator initialRouteName={"Landing"}>
                        <Stack.Screen name={"Landing"} component={LandingScreen} options={{headerShown:false}} />
                        <Stack.Screen name={"Register"} component={RegistrationScreen}/>
                        <Stack.Screen name={"Login"} component={LogInScreen}/>
                    </Stack.Navigator>
                </NavigationContainer>
            );
        }

        return <Provider store={store}>
            <NavigationContainer>
            <Stack.Navigator initialRouteName={"Main"}>
                <Stack.Screen name={"Main"} component={MainScreen} />
                <Stack.Screen name={"Add"} component={AddScreen} navigation={this.props.navigation} />
                <Stack.Screen name={"Save"} component={SaveScreen} navigation={this.props.navigation} />
            </Stack.Navigator>
            </NavigationContainer>
        </Provider>



    }


}
